    // });
    // }

    if (dealBtn) {
        dealBtn.addEventListener('click', function(event) {
            event.preventDefault();
            Deck.clearTable();
            Deck.build();
            Deck.shuffle(deckArr);
            Deck.dealBJ(deckArr);
            //give ability to build a new deck that is not shuffled
            isDeckBuilt = false;
        });
    }

    if (hitBtn) {
